      out.write("<!-- 图片预览 -->\r\n");
      out.write("<script>\r\n");
      out.write("//图片本地预览\r\n");
      out.write("function previewImage(file,targetid){\r\n");
      out.write("    if(checkPic(file)){\r\n");
      out.write("    \tvar MAXWIDTH  = 200;\r\n");
      out.write("        var MAXHEIGHT = 120;\r\n");
      out.write("        var img =$(file).parents(\"form\").first().find(\"#\"+targetid)[0];\r\n");
      out.write("        if (file.files && file.files[0]){\r\n");
      out.write("          img.onload = function(){\r\n");
      out.write("            img.width =MAXWIDTH;\r\n");
      out.write("            img.height = MAXHEIGHT;\r\n");
      out.write("          }\r\n");
      out.write("          var reader = new FileReader();\r\n");
      out.write("          reader.onload = function(evt){img.src = evt.target.result;}\r\n");
      out.write("          reader.readAsDataURL(file.files[0]);\r\n");
      out.write("        }else{\r\n");
      out.write("          var sFilter='filter:progid:DXImageTransform.Microsoft.AlphaImageLoader(sizingMethod=scale,src=\"';\r\n");
      out.write("          file.select();\r\n");
      out.write("          var src = document.selection.createRange().text;\r\n");
      out.write("          img.filters.item('DXImageTransform.Microsoft.AlphaImageLoader').src = src;\r\n");
      out.write("          div.innerHTML = \"<div id=divhead style='width:\"+MAXWIDTH+\"px;height:\"+MAXHEIGHT+\"px;\"+sFilter+src+\"\\\"'></div>\";\r\n");
      out.write("        }\r\n");
      out.write("    }\r\n");
      out.write("}\r\n");
      out.write("//图片删除\r\n");
      out.write("function delimage(filename,targetid,delname){\r\n");
      out.write("\tvar delurl=$(\"input[name='\"+delname+\"']\").val();\r\n");
