      out.write("\t\tbootbox.confirm('确认是否删除图片？',function(rs){\r\n");
      out.write(" \t\t\tif(rs){\r\n");
      out.write(" \t\t\t\tresetimage(filename,targetid,delname);\r\n");
      out.write(" \t\t\t\tvar url = \"files/delfiles\";\r\n");
      out.write(" \t\t\t\t$.post(url,\"delurl=\"+delurl,function callback(data){  \r\n");
      out.write(" \t\t\t\t\tvar resp = eval(\"(\"+data+\")\"); \r\n");
      out.write(" \t\t\t\t\t$(\"input[name='\"+delname+\"']\").val(\"\");\r\n");
      out.write(" \t\t\t\t\t//$(\"#saveBtn\").click();//保存一次数据\r\n");
      out.write(" \t\t\t\t});\r\n");
      out.write(" \t\t\t}\r\n");
      out.write(" \t\t});\r\n");
      out.write("\t}else{\r\n");
      out.write("\t\tresetimage(filename,targetid,delname);\r\n");
      out.write("\t}\r\n");
      out.write("}\r\n");
      out.write("//图片重置\r\n");
