      out.write("\tobj.after(obj.clone().val(\"\")); \r\n");
      out.write("\tobj.remove();\r\n");
      out.write("}\r\n");
      out.write("//图片选择格式限制\r\n");
      out.write("function checkPic(obj) {\r\n");
